// AccountDemo.java provides a simple test bed for the AccountKind package.//// Started by: Charles Hoot, for Hands On Java.// Date: July, 2000      // Finished by: Carl Stevenson// Purpose: To drive our bank account system//// **************************************************************import java.util.*;  // Scannerimport java.io.*;    // PrintStreamimport AccountKinds.*;class AccountDemo extends Object{  static PrintStream theScreen = new PrintStream(System.out);  static Scanner theKeyboard = new Scanner(System.in);	public static void main(String args[])	{		theScreen.println("\nWelcome to the Account package demonstrator!\n");		theScreen.print("\nCreating a Regular Account: ");		RegularAccount anAccount;		anAccount = new RegularAccount("The green hornet", "1202", 50.0);						theScreen.println("\nAccount status is: " + anAccount);				// Add test lines for Experiments here			theScreen.println("Depositing 100.00 ");		anAccount.deposit(100.00, "1202");		theScreen.println("\nAccount status is: " + anAccount);	   	theScreen.println("Withdrawing 90.00 ");		anAccount.withdraw(90.0, "1202");		theScreen.println("\nAccount status is: " + anAccount);	}} // end of class